CREATE TABLE users (
    userId INT IDENTITY(1,1) PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    phone VARCHAR(8) UNIQUE NOT NULL,
    dob DATE NOT NULL,
    password VARCHAR(255) NOT NULL,
    preferredLanguage VARCHAR(20) DEFAULT 'English',
    role VARCHAR(20) 
);

CREATE TABLE certificationtypes (
    certificationId INT IDENTITY(1,1) PRIMARY KEY,
    certificationName VARCHAR(100) UNIQUE NOT NULL
);

INSERT INTO certificationtypes (certificationName)
VALUES ('Trishaw Pilot Certification'),
       ('Cyclist Certification'),

CREATE TABLE usercertifications (
    id INT IDENTITY(1,1) PRIMARY KEY,
    userId INT NOT NULL FOREIGN KEY REFERENCES users(userId),
    certificationId INT NOT NULL FOREIGN KEY REFERENCES certificationtypes(certificationId),
    certifiedDate DATETIME DEFAULT GETDATE()
);

CREATE TABLE events (
eventId INT IDENTITY(1,1) PRIMARY KEY,
header VARCHAR(100) NOT NULL,
intro VARCHAR(1000) NOT NULL,
location VARCHAR(50) NOT NULL,
nearestMRT VARCHAR(200) NOT NULL,
latitude FLOAT NOT NULL,
longitude FLOAT NOT NULL,
radius_m INT NOT NULL DEFAULT 100, -- allowed radius in meters for clock in/out
start_time DATETIME NOT NULL,
end_time DATETIME NOT NULL,
longIntro VARCHAR(MAX) NOT NULL
)

INSERT INTO events (header, intro, location, nearestMRT, latitude, longitude, radius_m, start_time, end_time, longIntro) VALUES
('Trishaw Ride at East Coast Park', 'Enjoy a breezy seaside ride and take in the coastal views with our volunteer pilots.', 'East Coast Park', 'Marine Terrace MRT', 1.3069, 103.9125, 100, '2025-11-10 08:00:00', '2025-11-10 10:00:00', 'East Coast Park is one of Singapore’s most iconic outdoor destinations, and this trishaw ride is designed to offer seniors a relaxed and enjoyable coastal experience. As the trishaw moves along the park’s wide cycling paths, participants can enjoy uninterrupted seaside views, gentle ocean breezes, and the calming sound of waves. The route is intentionally paced to ensure comfort, allowing riders to fully appreciate the surroundings without any physical strain. Volunteer pilots accompanying the ride are trained to provide friendly conversation, assistance, and guidance, helping seniors feel supported throughout the journey. The event also creates opportunities to meet new people, reminisce about past visits to East Coast Park, and enjoy time outdoors in a safe and meaningful way. Whether it is the memories of family picnics, the sight of ships anchored off the coast, or the joy of simply being in nature, this trishaw ride offers a refreshing escape from everyday routines. By the end of the journey, participants often feel relaxed, uplifted, and reconnected with the natural beauty of Singapore’s coastline, making it a perfect outing for seniors seeking fresh air and pleasant company.'),
('Trishaw Ride through Chinatown', 'Experience the heritage streets of Chinatown from the comfort of a trishaw.', 'Chinatown', 'Chinatown MRT', 1.2846, 103.8435, 100, '2025-11-11 09:00:00', '2025-11-11 11:00:00', 'This trishaw ride through Chinatown offers seniors a nostalgic and immersive journey through one of Singapore’s oldest cultural districts. As the trishaw glides past shophouses, temples, and bustling market streets, participants can enjoy a slow and comfortable tour without needing to walk long distances. Volunteer pilots will share stories about Chinatown’s evolution—from early migrant settlements to the vibrant heritage site it is today—helping seniors relive moments from their own past or discover cultural elements they may have missed. The route highlights iconic landmarks such as the Buddha Tooth Relic Temple, Sri Mariamman Temple, and the Chinatown Street Market. Seniors will also enjoy observing local merchants, traditional medicine halls, and lantern-lined streets. The ride is designed to be both engaging and relaxing, offering time for passengers to take in intricate architectural details and the lively atmosphere around them. Whether participants have deep personal memories of the area or are simply exploring it from a new perspective, this trishaw journey delivers a meaningful cultural experience. By connecting seniors with Singapore’s living heritage in a safe and welcoming way, the Chinatown trishaw ride encourages community bonding, storytelling, and appreciation for the rich cultural tapestry of the nation.'),
('Morning Trishaw Tour at Gardens by the Bay', 'Ride past the Supertrees and Flower Dome in a relaxing morning outing.', 'Gardens by the Bay', 'Gardens by the Bay MRT', 1.2816, 103.8636, 100, '2025-11-12 08:30:00', '2025-11-12 11:30:00', 'A morning trishaw tour at Gardens by the Bay is the perfect way for seniors to experience the freshness and beauty of one of Singapore’s most iconic attractions without the physical effort of walking through the vast garden grounds. This calm and scenic ride begins at a comfortable pace, allowing participants to enjoy the early morning coolness while admiring the lush greenery and creative landscaping around them. The route includes views of the towering Supertrees, the Flower Dome, and the Cloud Forest exterior, creating plenty of opportunities to appreciate nature and architecture in harmony. Volunteer pilots accompany participants, providing friendly conversations and guidance throughout the journey. For many seniors, this ride offers a rejuvenating escape—a chance to reconnect with nature, breathe fresh air, and engage the senses. The tranquil paths and blooming flora help promote relaxation and mental well-being. The slow, guided experience also encourages participants to take in the details of the surroundings, from unique plant species to the gentle sounds of water features. As seniors complete the tour, they often feel uplifted, refreshed, and inspired by the beauty and serenity that Gardens by the Bay has to offer.'),
('Trishaw Ride around Marina Bay', 'Take in the skyline and waterfront sights with a scenic Marina Bay circuit.', 'Marina Bay', 'Bayfront MRT', 1.2834, 103.8606, 100, '2025-11-13 09:00:00', '2025-11-13 12:00:00', 'This trishaw ride around Marina Bay provides seniors with a relaxing and scenic experience of Singapore’s world-famous waterfront district. The smooth and wide promenades are ideal for trishaw travel, allowing riders to enjoy unobstructed views of the skyline, iconic landmarks, and the calm waters of the bay. From the ArtScience Museum to the Marina Bay Sands towers and the Esplanade, participants can appreciate the architectural beauty of modern Singapore at a comfortable pace. Volunteer pilots help ensure a safe, pleasant, and enjoyable outing, offering light conversation and helpful assistance throughout the journey. Seniors will have the chance to reconnect with familiar sights while also discovering newer additions to the Marina Bay area. The refreshing breeze and open views make it a soothing outdoor experience, ideal for relieving stress and lifting spirits. This trishaw ride also promotes social interaction and shared enjoyment, encouraging laughter, stories, and bonding among participants and volunteers. By the end of the route, seniors often feel energized and delighted by the vibrant cityscape, making this event a memorable highlight for anyone seeking a beautiful and gentle urban adventure.'),
('Community Trishaw Ride at Bishan Park', 'Join us for a gentle park ride with friendly volunteer pilots.', 'Bishan-Ang Mo Kio Park', 'Bishan MRT', 1.3610, 103.8250, 100, '2025-11-14 08:30:00', '2025-11-14 11:30:00', 'The community trishaw ride at Bishan-Ang Mo Kio Park brings seniors into one of Singapore’s most beloved and expansive heartland parks. This event is designed to offer a gentle, enjoyable, and socially engaging outdoor experience for participants of all mobility levels. As the trishaw moves along the park’s serene riverside paths, seniors can enjoy the soothing atmosphere created by flowing water, mature trees, and open green fields. Volunteer pilots accompany each participant, ensuring comfort, safety, and warm company throughout the journey. Bishan Park is known for its naturalized river, wildlife sightings, and peaceful walking trails, making it an ideal location for a rejuvenating ride. Many seniors appreciate the opportunity to revisit a familiar place, reflect on fond memories, or simply relax in the fresh air. Beyond the scenery, this ride also fosters community bonding by connecting seniors with caring volunteers and fellow participants. The shared conversational moments and cheerful interactions help build confidence and social connection. By the end of the event, riders often feel revitalized and uplifted, carrying with them a renewed appreciation for nature, companionship, and the joyful simplicity of outdoor activities.'),
('Trishaw Ride at Sentosa Boardwalk', 'Enjoy the ocean breeze and island views on a relaxing trishaw trip.', 'Sentosa Boardwalk', 'HarbourFront MRT', 1.2524, 103.8208, 100, '2025-11-15 09:30:00', '2025-11-15 12:30:00', 'The Sentosa Boardwalk Trishaw Ride gives seniors a rare opportunity to enjoy island views without the need for strenuous walking. As the trishaw glides along the boardwalk, riders are treated to scenic panoramas of the harbour, lush greenery, and the beautiful waterfront leading into Sentosa. The route is wide, sheltered in many parts, and designed for a comfortable, breezy ride. Volunteer pilots accompany seniors throughout the session, ensuring a safe and relaxing experience while sharing interesting stories about the island’s history and development. Many participants enjoy watching cable cars glide overhead, spotting ships passing through the channel, and observing visitors heading into Sentosa attractions. The gentle pace allows seniors to take in the small details—from the unique architectural design of the boardwalk to the surrounding tropical landscape. This ride also promotes emotional well-being by offering moments of tranquility and connection with nature. At the same time, the event encourages social interaction as seniors chat with volunteers and fellow riders. By the end of the journey, participants often feel refreshed and inspired, having enjoyed a mini island-style getaway right at the doorstep of the city. It’s an ideal outing for seniors who love scenic views and a peaceful sea breeze.'),
('Trishaw Heritage Trail in Kampong Glam', 'Discover the stories of old Singapore on a guided Kampong Glam route.', 'Kampong Glam', 'Bugis MRT', 1.3025, 103.8587, 100, '2025-11-16 09:00:00', '2025-11-16 12:00:00', 'The Kampong Glam trishaw heritage trail invites seniors to rediscover one of Singapore’s most culturally rich districts in a comfortable and meaningful way. This slow-paced ride takes participants past iconic landmarks such as Sultan Mosque, the Malay Heritage Centre, and colourful conserved shophouses filled with traditional crafts and Middle Eastern influences. Volunteer pilots accompany seniors throughout the experience, offering friendly conversation and sharing stories about the area’s historical importance as a royal and cultural centre for the Malay community. The route allows riders to appreciate the vibrant street art, heritage architecture, and lively atmosphere of Bussorah Street, Haji Lane, and Arab Street—all without the challenges of walking long distances. As the trishaw gently moves through the neighbourhood, seniors can observe the blend of old and new, from traditional textile shops to trendy cafés and boutique stores. The event fosters connection, nostalgia, and cultural appreciation, giving older participants a chance to revisit memories or learn about traditions they might not be familiar with. By the end of the trail, many seniors feel enriched and inspired, having enjoyed a sensory and historical journey through one of Singapore’s most beloved heritage zones.'),
('Evening Trishaw Ride along Singapore River', 'See the city lights from Clarke Quay to Boat Quay at dusk.', 'Singapore River', 'Clarke Quay MRT', 1.2897, 103.8501, 100, '2025-11-17 18:30:00', '2025-11-17 20:30:00', 'An evening trishaw ride along the Singapore River offers seniors a magical way to experience the city’s beauty as the lights come alive at dusk. This gentle and scenic journey begins at Clarke Quay and continues along Boat Quay and the riverside promenade, offering beautiful reflections of heritage bridges, colonial buildings, and modern skyscrapers on the water. Volunteer pilots accompany participants and ensure a comfortable, safe, and pleasant ride, while also sharing interesting stories about the river’s transformation from a bustling trading port to a lively and iconic waterfront destination. The cool evening air creates a calm and relaxing environment, making it perfect for seniors who enjoy peaceful outdoor activities. Along the way, riders may spot bumboats, riverside diners, and lively entertainment spots, adding vibrancy to the atmosphere. The contrast of soft lighting, flowing water, and historical architecture makes this ride visually captivating and emotionally soothing. Seniors often enjoy reminiscing about the river’s past or simply soaking in the ambience with fellow participants. This event provides a wonderful blend of heritage, scenery, and companionship, leaving seniors feeling refreshed and connected to Singapore’s evolving urban story.'),
('Trishaw Ride through Toa Payoh Town Park', 'Ride through the tranquil park and relive memories of Singapore''s heartland.', 'Toa Payoh Town Park', 'Toa Payoh MRT', 1.3343, 103.8563, 100, '2025-11-18 08:00:00', '2025-11-18 10:00:00', 'This trishaw ride through Toa Payoh Town Park provides seniors with a peaceful and nostalgic escape into one of Singapore’s earliest landscaped public parks. The gentle ride takes participants past familiar landmarks such as the park’s iconic ponds, stone bridges, and lush greenery, all of which have been carefully preserved over the years. Volunteer pilots accompany seniors, ensuring a relaxing and safe experience as they guide the trishaw through shaded pathways and open landscaped areas. For many older Singaporeans, Toa Payoh Town Park holds special memories—from weekend strolls and family picnics to community events and early courtship days. This trishaw ride gives participants a chance to revisit those memories in comfort. The calm atmosphere, chirping birds, and beautifully maintained foliage offer a soothing environment ideal for mental relaxation and emotional well-being. The ride also promotes social interaction as seniors enjoy conversations with volunteers and fellow riders. Whether reliving personal stories or simply admiring the park’s timeless charm, participants often leave the event feeling uplifted and refreshed. The Toa Payoh Town Park trishaw ride is a heartwarming and meaningful experience that reconnects seniors with nature, community, and treasured memories.'),
('Morning Trishaw Tour at Botanic Gardens', 'A peaceful nature ride through Singapore''s only UNESCO World Heritage Site.', 'Singapore Botanic Gardens', 'Botanic Gardens MRT', 1.3150, 103.8162, 100, '2025-11-19 09:00:00', '2025-11-19 12:00:00', 'A morning trishaw tour at the Singapore Botanic Gardens provides seniors with a tranquil escape into the nation’s only UNESCO World Heritage Site. This peaceful ride takes participants through beautifully maintained landscapes, including lush rainforest zones, serene ponds, and wide open lawns. The early morning timing ensures a comfortable climate, allowing seniors to enjoy fresh air and gentle sunlight without physical exertion. Volunteer pilots accompany participants throughout the journey, ensuring safety, comfort, and warm companionship. Many seniors appreciate the opportunity to reconnect with nature and observe unique plant varieties and wildlife, such as swans, turtles, and colourful birds. The slow pace of the trishaw allows them to take in the details of heritage trees, floral displays, and historic garden structures that they might otherwise miss when walking. The immersive natural environment helps reduce stress and promotes emotional well-being. At the same time, the shared experience fosters conversation and bonding with volunteers and fellow riders. By the end of the tour, many seniors feel renewed, peaceful, and inspired, having spent their morning surrounded by nature’s beauty in one of Singapore’s most cherished locations.'),
('Trishaw Ride at Jurong Lake Gardens', 'Explore the scenic lakeside gardens on a comfortable trishaw.', 'Jurong Lake Gardens', 'Lakeside MRT', 1.3356, 103.7334, 100, '2025-11-20 08:30:00', '2025-11-20 11:30:00', 'The Jurong Lake Gardens trishaw ride offers seniors a rejuvenating experience surrounded by serene lakeside scenery and beautifully landscaped nature spaces. As the trishaw moves along the wide, smooth pathways, participants can enjoy calm views of the water, lush green zones, and thoughtfully designed community areas that make the gardens such a beloved destination. Volunteer pilots accompany seniors throughout the ride, ensuring safety, comfort, and warm conversation while sharing insights about the gardens’ transformation into Singapore’s national gardens in the heartlands. The ride passes by scenic highlights such as Grasslands, Rasau Walk, and the tranquil water lily ponds, creating an immersive experience perfect for relaxation and nature appreciation. Seniors can also enjoy spotting resident wildlife including herons, dragonflies, butterflies, and terrapins near the shores. The slow pace of the trishaw allows participants to take in the peaceful environment at their own comfort level, making the outing ideal for those who may prefer gentle outdoor activities. Many seniors find the experience emotionally uplifting as they reconnect with nature and enjoy the companionship of volunteers and fellow riders. Whether reminiscing about past visits or discovering the gardens anew, this ride provides a refreshing and meaningful way to spend the morning.'),
('Trishaw Ride through Little India', 'Immerse in the colorful streets and aromas of Little India.', 'Little India', 'Little India MRT', 1.3064, 103.8518, 100, '2025-11-21 09:00:00', '2025-11-21 12:00:00', 'A trishaw ride through Little India offers seniors a vibrant, colourful, and culturally rich experience unlike any other. The moment the trishaw enters the district, riders are greeted with lively streets filled with traditional shops, aromatic spices, fresh flower garlands, and beautifully decorated temples. Volunteer pilots accompany seniors throughout the route, offering friendly conversation and sharing stories about the area’s heritage, festivals, and long-standing Indian community. The slow and steady pace of the trishaw allows participants to appreciate the intricate details of shophouse designs, street artwork, and cultural landmarks such as Sri Veeramakaliamman Temple and Tekka Market. Seniors often enjoy the sensory experience—taking in the bright colours, festive decorations, and the warm community atmosphere that Little India is known for. This ride also encourages meaningful social connection, as the lively surroundings naturally spark conversation and shared enjoyment among participants. The experience is especially well-loved by seniors who enjoy cultural encounters or wish to reconnect with familiar memories from Singapore’s past. By the end of the ride, many participants leave with smiles, enriched by the energy, heritage, and warmth of this iconic neighbourhood. The Little India trishaw experience is truly a feast for the senses and the heart.'),
('Community Trishaw Day at Tampines Hub', 'Enjoy short trishaw loops around the hub with family and friends.', 'Our Tampines Hub', 'Tampines MRT', 1.3534, 103.9450, 100, '2025-11-22 09:30:00', '2025-11-22 12:30:00', 'The Community Trishaw Day at Our Tampines Hub is a lively and engaging event designed to bring seniors together in a friendly and cheerful environment. The ride features short loops around the hub and its surrounding community spaces, making it ideal for seniors who prefer shorter, lighter outings. Volunteer pilots accompany each participant and offer warm conversation, assistance, and encouragement throughout the session. As trishaws glide past sports facilities, gardens, and community plazas, riders can enjoy observing the vibrant daily life at Singapore’s largest integrated community hub. The event emphasises social participation, allowing seniors to interact with volunteers, fellow participants, and even curious residents who often wave as the trishaws pass by. Many seniors find joy in the sense of community spirit and inclusiveness that the event fosters. The ride also helps build confidence and promotes emotional well-being by encouraging seniors to stay active and engaged with their neighbourhood. For some participants, the outing brings back nostalgic memories of Tampines in earlier decades, while for others, it is an opportunity to explore the modern amenities of the hub. Overall, this event offers a heartwarming blend of fun, companionship, and community connection.'),
('Trishaw Ride along the Civic District', 'Pass by landmarks like the National Gallery and Padang.', 'Civic District', 'City Hall MRT', 1.2904, 103.8515, 100, '2025-11-23 09:00:00', '2025-11-23 12:00:00', 'The Civic District trishaw ride offers seniors a beautiful blend of heritage, architecture, and cultural landmarks located in the heart of Singapore. As the trishaw gently moves along the wide boulevards, participants can admire iconic buildings such as the National Gallery Singapore, the Asian Civilisations Museum, Victoria Theatre, and the historic Padang. Volunteer pilots accompany seniors throughout the journey, ensuring a comfortable, safe, and enjoyable experience while sharing interesting stories about Singapore’s early civic life and the transformation of these national monuments. The ride provides a rare opportunity to appreciate the district’s elegance at a relaxed pace, without the need for extensive walking. Seniors often enjoy observing the architectural details of the colonial-era buildings, the charming riverside scenery, and the modern skyline that rises behind these well-preserved heritage structures. The experience also encourages social engagement as participants chat with volunteers and fellow riders about memories, history, and shared impressions of the landmarks. Many seniors find the outing both educational and nostalgic, especially if they have personal connections to the area. By the end of the ride, participants leave with a sense of pride and appreciation for Singapore’s heritage and civic identity.'),
('Sunset Trishaw Ride at Marina Barrage', 'Catch the sunset and city skyline in one relaxing ride.', 'Marina Barrage', 'Marina Bay MRT', 1.2807, 103.8710, 100, '2025-11-24 17:30:00', '2025-11-24 19:30:00', 'A sunset trishaw ride at Marina Barrage offers seniors one of the most breathtaking views of Singapore’s skyline and waterfront. This relaxing experience begins in the late afternoon, allowing participants to enjoy the gradual transition from warm sunshine to the golden glow of sunset. Volunteer pilots accompany each senior throughout the route, ensuring comfort while sharing lighthearted conversation and stories about the barrage’s role in water sustainability and flood management. As the trishaw moves across the open spaces near the rooftop lawn and waterfront promenade, riders can admire panoramic views of Marina Bay Sands, the Singapore Flyer, and the city’s shimmering skyline. The cooling evening breeze adds to the peaceful and refreshing atmosphere, making the ride ideal for seniors who prefer quieter, scenic outings. Many participants enjoy watching kites flying, families relaxing on the lawn, and the sky changing colours as the sun dips below the horizon. This ride offers a moment of tranquillity and reflection, encouraging seniors to relax, connect with nature, and appreciate the beauty of Singapore at dusk. By the end of the journey, participants often feel uplifted and grateful for the shared experience.'),
('Trishaw Ride at Pasir Ris Park', 'Ride through mangrove trails and seaside paths.', 'Pasir Ris Park', 'Pasir Ris MRT', 1.3817, 103.9475, 100, '2025-11-25 08:30:00', '2025-11-25 11:30:00', 'The Trishaw Ride at Pasir Ris Park offers seniors an uplifting and refreshing morning experience surrounded by coastal breeze, greenery, and wildlife. As the ride begins, participants are gently taken through the park’s iconic mangrove boardwalks, where the smell of brackish water and the calming sounds of rustling leaves immediately set a peaceful tone. Mangrove trees line the route, and riders may spot mudskippers, herons, or even the occasional monitor lizard basking in the sun. Moving toward the seaside paths, the trishaw glides alongside long stretches of sandy shoreline, giving seniors a chance to enjoy open views of the sea and passing ships. Volunteers engage riders with friendly conversation, pointing out unique plants, scenic photo spots, and park features such as the big playground and lookout decks. With the morning sun casting a warm but gentle glow, the ride aims to uplift spirits, encourage relaxation, and provide meaningful interaction. Whether participants come alone or with family, the experience fosters connection, nature appreciation, and a sense of adventure that remains comfortable and safe for all.'),
('Morning Trishaw Ride at Punggol Waterway', 'Discover scenic bridges and waterways in Punggol.', 'Punggol Waterway Park', 'Punggol MRT', 1.4050, 103.9025, 100, '2025-11-26 08:00:00', '2025-11-26 11:00:00', 'The Morning Trishaw Ride at Punggol Waterway is designed to give seniors a calm, scenic, and memorable journey through one of Singapore’s most beautiful waterfront districts. As the ride starts, participants are greeted by cool morning air and the soothing sounds of flowing water along the iconic Punggol Waterway Canal. The route passes over several scenic bridges, including the Jewel Bridge and Wave Bridge, each offering panoramic views perfect for photographs and quiet appreciation. Volunteers gently pedal through landscaped paths lined with tall trees, flowering bushes, and open spaces where birds such as kingfishers and herons often appear. Seniors will enjoy seeing joggers, families, and cyclists using the park, adding a sense of lively community to the experience. The ride highlights Punggol’s modern charm—its blend of nature, architecture, and waterfront living—while still providing peaceful moments for reflection. Participants can enjoy light conversations, learn about the area’s history, or simply relax while taking in the calming views. The trishaw ride helps seniors stay active, socially engaged, and connected with nature in a safe and enjoyable way.'),
('Trishaw Tour of Orchard Heritage Sites', 'See old and new Singapore along Orchard Road''s landmarks.', 'Orchard Road', 'Orchard MRT', 1.3048, 103.8318, 100, '2025-11-27 10:00:00', '2025-11-27 13:00:00', 'The Trishaw Tour of Orchard Heritage Sites takes seniors on a fascinating journey through one of Singapore’s most iconic districts, blending historical charm with modern vibrancy. Although Orchard Road is now known for its world-famous shopping malls and bustling streets, it began as a quiet area lined with nutmeg plantations, fruit orchards, and colonial residences. This trishaw ride allows seniors to rediscover the road’s roots while enjoying a comfortable, leisurely tour. The route passes landmarks such as the Istana’s outer grounds, heritage trees, old churches, and conserved buildings tucked among the modern high-rises. Volunteers point out interesting facts, such as how Orchard Road once had a canal running through its center and how various communities shaped the district over the decades. Amid the buzz of the area, riders get to enjoy the contrast of lush greenery, historical architecture, and contemporary lifestyle scenes. The slower pace of the trishaw helps seniors appreciate details often missed on foot. The ride is both nostalgic and refreshing, offering a blend of heritage storytelling, outdoor enjoyment, and vibrant city energy.'),
('Trishaw Ride through Fort Canning Park', 'A gentle ride through lush greenery and historic spots.', 'Fort Canning Park', 'Fort Canning MRT', 1.2924, 103.8464, 100, '2025-11-28 09:00:00', '2025-11-28 12:00:00', 'The Trishaw Ride through Fort Canning Park offers seniors an immersive blend of lush greenery, cultural heritage, and gentle slopes perfect for a relaxing outing. Fort Canning Park is one of Singapore’s most historically rich locations, having served as the seat of ancient Malay kings, a British military base, and a key landmark during World War II. The trishaw ride introduces seniors to this unique past while keeping the journey comfortable and serene. As the ride begins, participants are surrounded by towering rain trees, vibrant plants, and quiet forested paths. Volunteers may point out landmarks such as the Fort Gate, the ASEAN Sculpture Garden, Spice Garden, and Battlebox entrance. Birds and squirrels are often spotted among the foliage, adding a lively touch to the environment. Seniors will also enjoy the cool shade offered by the park’s dense canopy. The ride balances history, nature, and storytelling, helping participants reconnect with Singapore’s roots while enjoying meaningful interaction with volunteers. It is a gentle, uplifting activity suitable for all mobility levels.'),
('Evening Trishaw Ride at Esplanade', 'Enjoy music and waterfront views along Esplanade Drive.', 'Esplanade Park', 'Esplanade MRT', 1.2896, 103.8551, 100, '2025-11-29 18:00:00', '2025-11-29 20:00:00', 'The Evening Trishaw Ride at Esplanade invites seniors to enjoy Singapore’s iconic waterfront at its most magical time of day—sunset into evening. As the sky shifts from warm orange to deep blue, the skyline lights begin to glow, creating a beautiful backdrop for the ride. Starting from Esplanade Park, the trishaw travels along Esplanade Drive, passing familiar landmarks such as the Esplanade Theatres on the Bay, the durian-shaped arts center, and the sweeping waterfront promenade. The soft evening breeze keeps the ride cool and relaxing, while volunteers share light stories about the area’s arts scene, historical bridges, and the evolution of Marina Bay. Seniors can enjoy the faint sounds of musicians, buskers, and waterfront activity as the ride continues. The reflections of lights on the calm waters create a soothing atmosphere that encourages relaxation and joy. Whether participants prefer conversation or quiet appreciation, the ride ensures a peaceful evening experience filled with comfort, scenery, and connection.'),
('Trishaw Ride through Tiong Bahru', 'Experience nostalgic charm and heritage flats of Tiong Bahru.', 'Tiong Bahru Estate', 'Tiong Bahru MRT', 1.2857, 103.8265, 100, '2025-11-30 09:00:00', '2025-11-30 12:00:00', 'Experience the nostalgic charm of one of Singapore’s oldest and most beloved neighbourhoods with this relaxing trishaw ride through Tiong Bahru. Seniors will enjoy a comfortable, slow-paced journey past the iconic pre-war SIT flats, known for their unique Streamline Moderne architecture, gentle curves, and pastel tones that reflect Singapore’s early urban-planning style. The ride brings participants through quiet residential streets, cosy cafés, and well-kept community gardens that highlight the estate’s warm, neighbourly atmosphere. Volunteers will share stories about Tiong Bahru’s rich history, from its early days as Singapore’s first public housing estate to its transformation into a vibrant cultural spot while still retaining its timeless charm. The route is carefully chosen to ensure safety and comfort, avoiding steep slopes and heavy traffic. Seniors can enjoy the fresh morning breeze while reconnecting with memories of old Singapore or discovering hidden gems for the first time. This ride is perfect for seniors seeking a calm outing filled with heartfelt stories, heritage sights, and opportunities to engage with the local community.'),
('Trishaw Ride at Bedok Reservoir Park', 'A lakeside ride perfect for relaxation and birdwatching.', 'Bedok Reservoir Park', 'Bedok Reservoir MRT', 1.3362, 103.9125, 100, '2025-12-01 08:00:00', '2025-12-01 11:00:00', 'Enjoy a peaceful and refreshing trishaw ride around Bedok Reservoir Park, one of Singapore’s most scenic waterside locations. This event is designed especially for seniors seeking a relaxing outdoor experience surrounded by nature. The ride circles the sparkling reservoir, where gentle winds and wide open views create a calming atmosphere ideal for clearing the mind and enjoying the moment. Seniors will get the chance to admire the diverse wildlife commonly seen in the area, including herons, swans, and playful otters that occasionally surface near the shoreline. Volunteers will help explain the park’s history, its role as a former sand quarry, and how it has become a favourite hub for exercise and community gatherings. The ride route highlights accessible paths, shaded rest areas, and picturesque spots perfect for photos. The smooth terrain ensures a comfortable journey suitable for all mobility levels. This event provides a great way for seniors to enjoy nature, bond with volunteers, and experience the tranquillity of Singapore’s beloved reservoir parks.'),
('Trishaw Ride around Yishun Pond Park', 'Enjoy serenity and nature views at Yishun Pond.', 'Yishun Pond Park', 'Yishun MRT', 1.4293, 103.8350, 100, '2025-12-02 08:30:00', '2025-12-02 11:30:00', 'Discover the serenity of Yishun Pond Park through a scenic trishaw ride designed to provide comfort, peace, and a refreshing escape from the busy city. Seniors can look forward to an easygoing journey around the pond, where calm waters, lush greenery, and beautifully maintained walking paths create the perfect setting for a rejuvenating morning. The park’s circular route offers wide and unobstructed views of the water, complete with viewing decks where birds, butterflies, and dragonflies can often be spotted. Volunteers will share interesting facts about the park’s biodiversity and its connection to Khoo Teck Puat Hospital, a unique medical facility that integrates healing gardens and natural spaces into its design. The ride is intentionally paced to allow seniors to soak in the cool breeze, hear gentle sounds of nature, and enjoy the relaxing environment. With its peaceful atmosphere and focus on wellness, this trishaw ride is ideal for seniors who appreciate calm, nature-rich outings that promote both emotional and physical well-being.'),
('Trishaw Ride through Bukit Timah Nature Area', 'Explore lush greenery and historic rail corridors.', 'Bukit Timah Rail Corridor', 'Beauty World MRT', 1.3325, 103.7775, 100, '2025-12-03 09:00:00', '2025-12-03 12:00:00', 'Explore the green heart of Singapore through a slow and enjoyable trishaw ride along the Bukit Timah Nature Area and historic Rail Corridor. This unique experience takes seniors through refreshing stretches of greenery, heritage railway structures, and scenic forested paths filled with natural sounds and fresh air. Volunteers will guide participants along accessible sections of the Rail Corridor, highlighting its early 20th-century origins as part of the old KTM railway and its transformation into one of Singapore’s most cherished nature trails. Along the way, seniors will see a variety of plants, tropical trees, and perhaps even local wildlife such as squirrels or colourful birds. The route avoids steep inclines to ensure a comfortable and safe journey. Seniors can enjoy the balance of nature and history while taking in the open landscape and rustic charm of the old railway bridges. This event is especially suitable for those who enjoy peaceful greenery, want to revisit meaningful places from the past, or simply wish to enjoy a refreshing ride in nature.'),
('Community Trishaw Rides at Ang Mo Kio', 'Meet friendly volunteers and enjoy the breeze of AMK.', 'Ang Mo Kio Central', 'Ang Mo Kio MRT', 1.3692, 103.8454, 100, '2025-12-04 09:30:00', '2025-12-04 12:30:00', 'Join us for a warm and community-focused trishaw ride through the lively town centre of Ang Mo Kio. This event aims to bring seniors closer to their neighbourhood while offering a relaxing outdoor experience guided by friendly volunteers. The ride begins at AMK Central, where bustling markets, open plazas, and well-loved food centres reflect the heart of this vibrant residential town. Seniors will be taken along comfortable pathways that pass by community parks, colourful HDB blocks, and familiar landmarks that many residents hold dear. Along the journey, volunteers will share light-hearted stories and fun facts about the town’s development, community spirit, and evolution over the years. The ride encourages interaction, laughter, and connection — creating opportunities for seniors to make new friends and reminisce about shared experiences. With smooth routes, wide walkways, and plenty of shaded areas, this trishaw ride offers a breezy and enjoyable way to explore Ang Mo Kio’s energetic yet homely environment.'),
('Trishaw Ride through West Coast Park', 'Ride by the playgrounds and seaside areas in comfort.', 'West Coast Park', 'Clementi MRT', 1.2936, 103.7589, 100, '2025-12-05 08:30:00', '2025-12-05 11:30:00', 'The Trishaw Ride through West Coast Park offers seniors a refreshing and scenic journey along Singapore’s expansive coastal park. The ride is designed to be gentle, allowing participants to enjoy the open sea breeze and panoramic views of the western coastline. Seniors will pass by playgrounds, barbecue pits, and wide green lawns where families and fitness enthusiasts gather, giving a sense of community and vibrancy. Volunteers guide the ride along safe, paved paths that wind through clusters of lush trees and open fields, providing shade and comfort throughout. The route includes stops at scenic viewpoints where riders can pause to admire the calm waters of the sea, watch small boats passing by, or take photographs. Along the way, volunteers share interesting facts about the park’s history as reclaimed land and its development as a recreational space for residents. This ride encourages seniors to connect with nature, enjoy gentle physical activity, and socialize with other participants. The peaceful coastal environment combined with friendly volunteer support makes the West Coast Park trishaw ride a rejuvenating experience for the body and mind.'),
('Trishaw Ride at Bukit Batok Town Park', 'Take a short ride around the famous "Little Guilin".', 'Bukit Batok Town Park', 'Bukit Gombak MRT', 1.3581, 103.7636, 100, '2025-12-06 09:00:00', '2025-12-06 11:00:00', 'The Trishaw Ride at Bukit Batok Town Park, also known as “Little Guilin,” provides seniors with an accessible and picturesque outdoor adventure. This ride takes participants around a stunning granite quarry lake, flanked by steep rock formations reminiscent of the famous Guilin mountains in China. The calm waters of the quarry reflect the greenery and cliffs, creating a peaceful and scenic backdrop for a relaxing ride. Volunteers ensure a smooth journey along paved pathways, making it safe for seniors to enjoy the sights without any strain. Along the route, riders can admire the lush trees, garden spots, and open fields that invite wildlife such as birds and butterflies. Volunteers also share stories about the park’s transformation from an abandoned quarry into a community recreational space, highlighting both its natural beauty and local heritage. Seniors will appreciate the gentle pace of the trishaw, the fresh morning air, and the chance to engage in light conversation with volunteers or fellow riders. The ride combines relaxation, sightseeing, and a subtle touch of local history, providing a well-rounded and memorable experience for participants seeking tranquility and enjoyment outdoors.'),
('Morning Trishaw Ride at Labrador Park', 'Explore coastal history and nature trails.', 'Labrador Nature Reserve', 'Labrador Park MRT', 1.2703, 103.8022, 100, '2025-12-07 08:00:00', '2025-12-07 11:00:00', 'The Morning Trishaw Ride at Labrador Park is a blend of history, coastal scenery, and gentle outdoor exercise tailored for seniors. This scenic route takes riders along the southern coast of Singapore, where the lush greenery of Labrador Nature Reserve meets the sparkling waters of the sea. Participants will pass remnants of Singapore’s coastal defenses, including old bunkers and artillery structures, offering a glimpse into the nation’s history during World War II. Volunteers guide the ride along smooth trails, ensuring comfort while pointing out historical markers, interesting flora, and unique rock formations along the coastline. The trishaw glides past well-maintained gardens, lookout points, and shaded resting areas that provide opportunities for pause and reflection. Along the way, seniors can enjoy the sound of waves gently lapping against the shore, the fresh sea breeze, and the occasional sighting of birds or monitor lizards in their natural habitat. This ride not only encourages light physical activity but also immerses participants in Singapore’s coastal heritage, combining nature, history, and social interaction in a safe and enjoyable outing.'),
('Trishaw Ride at Sengkang Riverside Park', 'Relaxing ride through fruit tree groves and calm riverside paths.', 'Sengkang Riverside Park', 'Sengkang MRT', 1.3917, 103.8936, 100, '2025-12-08 08:30:00', '2025-12-08 11:30:00', 'The Trishaw Ride at Sengkang Riverside Park is a serene journey designed for seniors who wish to connect with nature and enjoy calm riverside scenery. This ride follows paved and accessible trails winding around waterways, fruit tree groves, and landscaped gardens that enhance the peaceful atmosphere. Volunteers escort seniors along the route, pointing out the park’s ecological features, including native plants, birds, and other wildlife that inhabit the area. The route includes gentle curves and open spaces, making it suitable for those with limited mobility. Seniors can pause to enjoy the reflections of trees on the water, watch the occasional dragonfly hover near the riverbanks, or take photos of the scenic surroundings. The ride encourages social interaction, gentle physical activity, and mental relaxation. Volunteers often share anecdotes about the park’s design, history, and conservation efforts, making the experience both educational and enjoyable. The combination of natural beauty, flowing waterways, and calm outdoor ambiance ensures that participants leave feeling refreshed, engaged, and connected to Singapore’s green spaces.'),
('Trishaw Ride through Bukit Merah Central', 'Community ride through the heartland of Bukit Merah.', 'Bukit Merah Central', 'Redhill MRT', 1.2875, 103.8097, 100, '2025-12-09 09:00:00', '2025-12-09 12:00:00', 'The Trishaw Ride through Bukit Merah Central offers seniors a comfortable, leisurely tour through one of Singapore’s mature town centres. This ride highlights the area’s blend of urban amenities, community spaces, and open green corridors, providing both scenic views and a glimpse into everyday local life. Participants will travel along wide, safe paths that pass through public parks, residential precincts, and recreational spaces, allowing them to enjoy fresh air and gentle activity without any strain. Volunteers escort the trishaws and share interesting insights about Bukit Merah’s history, from its development as one of Singapore’s early housing estates to its modern-day community features. Seniors can appreciate the vibrant mix of architecture, colourful HDB flats, and nearby landmarks while engaging in light conversation or simply relaxing along the route. The ride also emphasizes community connection, offering the chance to meet fellow participants and volunteers in a friendly, inclusive environment. With the combination of accessible paths, cultural context, and greenery, this trishaw ride ensures a safe, enjoyable, and meaningful outdoor experience for seniors, fostering well-being, social engagement, and enjoyment of the local neighbourhood.'),
('Trishaw Ride at Changi Village', 'Take in the sea breeze and watch planes fly by.', 'Changi Village', 'Tanah Merah MRT', 1.3892, 103.9875, 100, '2025-12-10 08:30:00', '2025-12-10 11:30:00', 'The Trishaw Ride at Changi Village offers seniors a tranquil and scenic journey along Singapore’s eastern coastline. This ride combines the gentle breeze of the sea with the picturesque views of the Changi shoreline, making it an ideal outdoor activity for relaxation and wellness. Participants glide past the village’s heritage structures, quaint eateries, and open green spaces where locals often gather for morning exercises or leisurely strolls. As the ride approaches the beach and marina areas, seniors can enjoy the sight of boats gently bobbing in the water and planes flying overhead toward Changi Airport, creating a dynamic yet calming atmosphere. Volunteers guide the ride along safe and accessible paths, sharing stories about Changi Village’s rich history as a fishing village, its transformation into a vibrant community, and its surrounding natural habitats. The route emphasizes safety, comfort, and gentle engagement, allowing seniors to interact with volunteers, capture memorable photos, or simply relax while enjoying the coastal environment. This ride combines scenic beauty, community spirit, and light physical activity, making it a refreshing and meaningful experience for participants of all mobility levels.'),
('Evening Ride along Henderson Waves', 'A scenic sunset ride across Singapore''s tallest bridge.', 'Henderson Waves', 'HarbourFront MRT', 1.2750, 103.8197, 100, '2025-12-11 17:30:00', '2025-12-11 19:30:00', 'The Evening Ride along Henderson Waves provides seniors with a stunning sunset experience atop Singapore’s tallest pedestrian bridge. Spanning 274 meters and elevated 36 meters above Henderson Road, the bridge offers panoramic views of the surrounding Bukit Timah Forest, Mount Faber Park, and the southern coastline. The trishaw ride is designed for comfort and safety, with volunteers guiding seniors along smooth paths and gentle inclines. As the sun sets, riders can enjoy the changing hues of the sky reflecting off nearby greenery and the urban skyline in the distance. The bridge’s unique wave-like structure adds a sense of novelty and wonder to the experience. Along the route, volunteers share interesting insights about the area’s history, ecology, and the bridge’s architectural design. This ride encourages relaxation, mindfulness, and social interaction, providing a refreshing opportunity for seniors to appreciate natural beauty, capture memorable photographs, and enjoy the company of fellow participants. The combination of elevated views, tranquil surroundings, and volunteer support ensures a memorable evening outing that blends leisure, scenic exploration, and gentle exercise.'),
('Morning Ride at Coney Island', 'Discover rustic nature trails and sea views on trishaw.', 'Coney Island Park', 'Punggol MRT', 1.4092, 103.9225, 100, '2025-12-12 08:00:00', '2025-12-12 11:00:00', 'The Morning Ride at Coney Island Park offers seniors a serene and nature-immersed experience away from the urban bustle. This rustic trail takes participants along the island’s unspoiled beaches, natural forests, and open grasslands, providing a rare opportunity to connect with Singapore’s coastal ecosystem. Volunteers guide the trishaws along smooth, safe paths suitable for all mobility levels, pointing out native wildlife such as monitor lizards, birds, and various coastal plants along the way. Riders can enjoy the gentle sound of waves lapping against the shore, the fresh sea breeze, and the unbroken horizon views at sunrise. Informative commentary from volunteers highlights the island’s conservation efforts, history as a former offshore private estate, and its transformation into a protected natural park. Seniors have the chance to pause at scenic spots for photos, light stretching, or quiet reflection. The ride promotes wellness, engagement with nature, and social interaction among participants. It is an ideal experience for seniors seeking a refreshing morning activity, mental relaxation, and the enjoyment of Singapore’s hidden natural gems.'),
('Trishaw Ride through Kallang Riverside', 'Enjoy city and river views from Kallang Basin.', 'Kallang Riverside Park', 'Kallang MRT', 1.3003, 103.8736, 100, '2025-12-13 09:00:00', '2025-12-13 12:00:00', 'The Trishaw Ride through Kallang Riverside provides seniors with a gentle and scenic route along Singapore’s Kallang Basin. This area combines the vibrancy of the city with calming river views, offering a unique urban-nature experience. Participants glide along wide, safe pathways that follow the river’s edge, passing waterfront parks, sports facilities, and communal spaces where locals engage in jogging, kayaking, and other outdoor activities. Volunteers accompany seniors throughout the journey, highlighting points of interest such as historic bridges, the nearby National Stadium, and local birdlife that inhabits the riverbanks. The ride emphasizes accessibility and comfort, allowing seniors to relax while taking in the scenic panorama and gentle sounds of flowing water. Pausing areas along the route provide opportunities for conversation, photography, or simply soaking in the tranquil surroundings. This trishaw journey fosters social interaction, physical activity, and a renewed appreciation for Singapore’s urban waterway ecosystems. The combination of city views, natural elements, and volunteer guidance ensures an engaging, safe, and memorable ride for all participants.'),
('Trishaw Ride at Bukit Panjang Park', 'Join our volunteers for a cheerful morning outing.', 'Bukit Panjang Park', 'Bukit Panjang MRT', 1.3792, 103.7714, 100, '2025-12-14 08:30:00', '2025-12-14 11:30:00', 'The Trishaw Ride at Bukit Panjang Park is designed to offer seniors a cheerful, community-oriented outdoor experience. The ride traverses scenic trails, open lawns, and landscaped gardens, providing ample opportunity for participants to enjoy the fresh morning air and lush greenery. Volunteers escort the trishaws along accessible paths, ensuring a safe and comfortable journey for all mobility levels. The route highlights the park’s unique features, such as playgrounds, exercise stations, and natural ponds, offering seniors points of interest to pause, take photos, or simply appreciate the surroundings. Throughout the ride, volunteers engage participants with informative commentary about the park’s development, flora and fauna, and local community activities, enriching the experience with both educational and social value. The gentle pace encourages relaxation and wellness while fostering a sense of connection among seniors, volunteers, and the surrounding environment. This ride emphasizes social interaction, light physical activity, and the opportunity to explore familiar and new spaces within the Bukit Panjang neighbourhood. It provides a memorable morning outing that combines nature, community engagement, and the simple joy of riding a trishaw in a scenic and welcoming environment.'),
('Trishaw Ride at Marine Parade', 'Seaside ride filled with breeze and nostalgia.', 'Marine Parade Promenade', 'Marine Parade MRT', 1.3025, 103.9075, 100, '2025-12-15 09:00:00', '2025-12-15 12:00:00', 'The Trishaw Ride at Marine Parade Promenade offers seniors a delightful coastal journey along one of Singapore’s most scenic seaside stretches. This ride is designed to provide a nostalgic yet relaxing experience, where participants can enjoy the gentle sea breeze, soft sand, and panoramic views of the East Coast coastline. Volunteers guide seniors along safe and well-maintained paths that pass through lush park areas, playgrounds, and communal spaces where locals gather for morning exercises or leisurely strolls. The ride route highlights Marine Parade’s rich heritage, from its early development as a seaside residential estate to its current status as a vibrant recreational hub. Along the way, participants can spot coastal wildlife, admire open spaces for relaxation, and learn about the area’s history from the friendly volunteers. The trishaw’s slow pace ensures seniors can take in the surroundings, pause at scenic viewpoints for photos, or simply enjoy the rhythmic motion and sounds of the ocean. This experience blends gentle physical activity, social engagement, and immersion in nature, creating a refreshing and memorable outing for seniors seeking a balance of recreation, relaxation, and cultural appreciation.'),
('Trishaw Ride through Queenstown Heritage Trail', 'Visit Singapore''s first satellite town by trishaw.', 'Queenstown', 'Queenstown MRT', 1.2944, 103.8058, 100, '2025-12-16 09:30:00', '2025-12-16 12:30:00', 'The Trishaw Ride through Queenstown Heritage Trail offers seniors a captivating journey through Singapore’s first satellite town. This route is carefully planned to provide safe, accessible paths while highlighting Queenstown’s rich architectural and cultural history. Participants will ride past iconic landmarks, including early HDB flats, community centres, and heritage streets that showcase the town’s transformation over decades. Volunteers escort seniors and share engaging stories about Queenstown’s development, from its post-war housing initiatives to modern community spaces, giving participants both historical insight and a sense of connection to the neighbourhood. The ride combines gentle physical activity with sightseeing, providing opportunities for seniors to enjoy fresh air, observe daily life, and appreciate the thoughtfully designed urban environment. Participants will also encounter green parks, quiet walkways, and shaded resting areas that allow for breaks or light social interaction. This trishaw experience is ideal for seniors who enjoy leisurely rides enriched with history, culture, and community engagement, offering both relaxation and mental stimulation while exploring one of Singapore’s most significant residential landmarks.'),
('Trishaw Ride at Tampines Eco Green', 'Enjoy peaceful greenery on eco-friendly paths.', 'Tampines Eco Green', 'Tampines MRT', 1.3575, 103.9367, 100, '2025-12-17 08:00:00', '2025-12-17 11:00:00', 'The Trishaw Ride at Tampines Eco Green offers seniors a peaceful and rejuvenating outdoor experience, surrounded by lush greenery and thoughtfully designed eco-friendly pathways. This ride follows winding trails through the park’s scenic gardens, water features, and native tree groves, providing a calm and restorative environment for all participants. Volunteers guide seniors along safe, smooth paths, highlighting the park’s sustainable design and educational features, such as rain gardens, biodiversity areas, and habitats for local wildlife. Riders can enjoy gentle breezes, the fragrance of flowers, and the sight of birds and butterflies in their natural settings. The pace is deliberately slow, allowing seniors to engage with the surroundings, pause at scenic spots, and take photographs if desired. Along the ride, volunteers also share interesting information about the park’s environmental initiatives, its role in community wellness, and the importance of green spaces in urban life. This experience combines light physical activity, nature appreciation, and social interaction, providing seniors with an enjoyable and meaningful morning excursion that nurtures both body and mind.'),
('Sunrise Ride at Changi Boardwalk', 'Start your day with a coastal sunrise trishaw ride.', 'Changi Boardwalk', 'Tanah Merah MRT', 1.3897, 103.9892, 100, '2025-12-18 06:45:00', '2025-12-18 08:45:00', 'Start the day with a Sunrise Ride at Changi Boardwalk, a coastal trishaw experience that immerses seniors in nature and tranquility. The ride follows accessible paths along the boardwalk, offering unobstructed views of the sea, soft morning sunlight, and gentle sea breezes. Volunteers accompany participants to ensure safety and provide information about the area’s natural environment, such as coastal vegetation, birdlife, and the marine ecosystem. Seniors can pause at scenic vantage points to watch the sun rise over the horizon, capturing the moment in photographs or simply enjoying the serenity. The route is designed to be comfortable for all mobility levels, with smooth paths and minimal inclines. Along the way, volunteers also share historical insights about Changi’s coastal development, the area’s significance during Singapore’s early years, and community initiatives to maintain the natural beauty of the shoreline. This ride offers gentle physical activity, mindfulness, and social engagement, fostering wellness and enjoyment in a peaceful morning setting. It is an ideal outing for seniors who appreciate nature, coastal landscapes, and the simple pleasure of starting the day with fresh air and scenic surroundings.'),
('Trishaw Ride at Thomson Nature Park', 'Discover Singapore''s forest trails and wildlife.', 'Thomson Nature Park', 'Marymount MRT', 1.3750, 103.8325, 100, '2025-12-19 08:00:00', '2025-12-19 11:00:00', 'The Trishaw Ride at Thomson Nature Park provides seniors with an immersive experience in one of Singapore’s serene forested areas. The ride takes participants along well-maintained trails winding through lush greenery, mature trees, and tranquil water features. Volunteers accompany seniors to ensure safety while sharing information about the park’s flora, fauna, and conservation efforts. Riders can enjoy the fresh scent of tropical plants, observe native wildlife such as birds, squirrels, and insects, and appreciate the peaceful ambiance of a forested urban park. The route is carefully chosen to be gentle, avoiding steep inclines and rough terrain, allowing seniors to relax and enjoy the surroundings. The ride encourages physical activity at a comfortable pace while providing opportunities for social interaction and engagement with the environment. Along the way, volunteers highlight interesting ecological facts, the park’s history as part of Singapore’s green infrastructure, and efforts to protect biodiversity. This trishaw experience combines recreation, learning, and wellness, making it a refreshing and enriching outing for seniors who seek connection with nature in a safe and enjoyable setting.'),
('Trishaw Ride at Bishan Active Park', 'Community fun ride with refreshments after.', 'Bishan Park', 'Bishan MRT', 1.3610, 103.8250, 100, '2025-12-20 09:00:00', '2025-12-20 12:00:00', 'The Trishaw Ride at Bishan Active Park offers seniors a vibrant and community-focused outdoor experience. This ride winds through Bishan Park, one of Singapore’s largest urban parks, featuring wide green lawns, jogging paths, and serene waterways. Participants are guided by volunteers along safe and accessible paths, ensuring comfort for seniors of all mobility levels. Riders can enjoy the lush greenery, listen to birdsong, and observe local flora along the riverbanks, creating a relaxing environment that promotes mental well-being. The ride also highlights the park’s innovative landscaping, including meandering streams, pedestrian bridges, and open recreational spaces that encourage social interaction. Volunteers provide interesting commentary about the park’s history, development, and its role as a hub for community engagement. After the ride, participants can enjoy light refreshments, fostering camaraderie and conversation among seniors and volunteers. This experience blends gentle physical activity, nature appreciation, and social interaction, offering a wholesome outing that supports both physical and mental health. The combination of scenic routes, volunteer guidance, and communal engagement makes the Bishan Active Park trishaw ride a memorable and enriching activity for seniors seeking leisure, wellness, and connection with nature.'),
('Trishaw Ride through Clementi Woods', 'A calm ride beneath the canopy of Clementi Woods.', 'Clementi Woods Park', 'Clementi MRT', 1.3075, 103.7642, 100, '2025-12-21 08:30:00', '2025-12-21 11:30:00', 'The Trishaw Ride through Clementi Woods Park provides seniors with a calm, nature-focused experience amidst Singapore’s verdant woodlands. The ride follows shaded pathways beneath tall trees, offering a tranquil canopy that protects riders from the sun while allowing them to enjoy the fresh forest air. Volunteers escort seniors on gentle trails that wind through the park, pointing out native plants, small wildlife, and the park’s natural features. Riders can pause at scenic spots to relax, take photographs, or observe birds and butterflies in their habitats. The route emphasizes safety, accessibility, and comfort, making it suitable for seniors of all mobility levels. Along the ride, volunteers share insights into the park’s history, its ecological importance, and the efforts taken to maintain biodiversity in urban green spaces. This ride encourages mental relaxation, light physical activity, and social interaction, creating an engaging and rejuvenating experience. The combination of quiet woodland trails, knowledgeable volunteer support, and opportunities for reflection ensures that participants leave feeling refreshed, connected to nature, and uplifted by the beauty of Singapore’s forested environments.'),
('Trishaw Ride along Rower''s Bay', 'Scenic lakeside route with birdwatching stops.', 'Rower''s Bay Park', 'Sengkang MRT', 1.4058, 103.8675, 100, '2025-12-22 08:00:00', '2025-12-22 11:00:00', 'The Trishaw Ride along Rower’s Bay Park is a scenic and serene journey that allows seniors to enjoy lakeside views and engage with local wildlife. The route follows well-paved trails around calm water bodies, providing opportunities for gentle exercise, birdwatching, and photography. Volunteers guide participants along safe paths and share insights into the local ecosystem, including native plants, migratory birds, and aquatic life inhabiting the area. The ride emphasizes accessibility and comfort, allowing seniors to relax and soak in the tranquil environment at a leisurely pace. Along the way, riders pass open green spaces where community members practice tai chi, jog, or enjoy picnics, offering a sense of vibrancy and social connection. Volunteers also highlight the park’s development, conservation efforts, and its importance in providing recreational opportunities for surrounding neighbourhoods. The experience combines nature immersion, mild physical activity, and social interaction, creating a wholesome outing that nurtures both physical and mental well-being. Seniors are encouraged to pause at scenic spots, take photographs, and enjoy casual conversations with volunteers or fellow riders. The Rower’s Bay trishaw ride provides a refreshing escape from urban life, leaving participants relaxed, engaged, and connected to Singapore’s natural beauty.'),
('Trishaw Ride at Kranji Countryside', 'Enjoy a countryside trishaw ride through local farms.', 'Kranji Countryside', 'Kranji MRT', 1.4325, 103.7525, 100, '2025-12-23 09:00:00', '2025-12-23 12:00:00', 'The Trishaw Ride at Kranji Countryside offers seniors a delightful countryside experience on the outskirts of Singapore. The ride follows quiet, scenic roads that pass local farms, vegetable plots, and open fields, providing participants with a sense of rural charm and tranquility. Volunteers guide seniors along safe and accessible paths, highlighting the area’s agricultural practices, seasonal produce, and the history of local farming communities. Riders can enjoy the fresh air, observe farm animals, and take photographs of the scenic landscape along the way. The gentle pace ensures comfort and safety for seniors while offering opportunities for social interaction and educational insights into sustainable farming and Singapore’s food heritage. Along the ride, volunteers share stories about Kranji’s evolution from traditional farmland to its current role as a hub for agritourism and community engagement. The combination of physical activity, scenic beauty, and interaction with knowledgeable volunteers creates an enjoyable and enriching experience. Seniors can relax, breathe in the countryside air, and appreciate the slower pace of life, making this trishaw ride a memorable outing that fosters wellness, learning, and connection with both nature and local culture.'),
('Trishaw Ride at Henderson Park Connector', 'Gentle group ride linking southern park trails.', 'Henderson Park Connector', 'HarbourFront MRT', 1.2886, 103.8192, 100, '2025-12-24 09:30:00', '2025-12-24 12:30:00', 'The Trishaw Ride at Henderson Park Connector provides seniors with a gentle, scenic route linking several southern parks in Singapore. The ride follows accessible, well-maintained trails that wind through greenery, connecting Mount Faber, Telok Blangah Hill Park, and surrounding park connectors. Volunteers accompany seniors to ensure safety and provide interesting commentary about the area’s history, flora, and fauna. Riders enjoy panoramic views of the city skyline, the harbor, and lush forested areas while traversing gentle slopes and shaded paths. The route emphasizes relaxation, social interaction, and light physical activity, allowing seniors to experience Singapore’s southern park network without strain. Volunteers encourage participants to pause at scenic viewpoints, capture photographs, or simply appreciate the tranquility of the environment. This trishaw ride also fosters community engagement, as seniors can converse with volunteers and fellow riders, building camaraderie and enjoying shared experiences in nature. By combining scenic vistas, gentle exercise, and educational insights about Singapore’s green spaces, the Henderson Park Connector trishaw ride offers a wholesome, rejuvenating, and memorable experience for seniors seeking outdoor recreation, wellness, and connection with both nature and their community.'),
('Trishaw Ride through Holland Village', 'A nostalgic ride through cafes and shop houses.', 'Holland Village', 'Holland Village MRT', 1.3117, 103.7964, 100, '2025-12-26 10:00:00', '2025-12-26 13:00:00', 'The Trishaw Ride through Holland Village offers seniors a charming journey through one of Singapore’s most iconic neighbourhoods. This route highlights the area’s nostalgic charm, with heritage shop houses, quaint cafes, and pedestrian-friendly streets. Volunteers escort seniors along safe and accessible paths, ensuring a comfortable pace suitable for all participants. Riders can enjoy the sights and sounds of local life, from bustling coffee shops to open-air markets, experiencing both the cultural history and modern vibrancy of the area. Along the way, volunteers share stories about Holland Village’s development, its role as a social hub, and the unique architecture of its buildings. The ride encourages engagement with the environment, gentle physical activity, and social interaction among seniors and volunteers. Scenic pauses allow participants to take photographs, appreciate architectural details, or simply enjoy the atmosphere. This experience blends culture, history, and leisure in a safe, guided setting, offering seniors a memorable way to explore one of Singapore’s most beloved neighbourhoods while connecting with volunteers and fellow riders.'),
('Trishaw Ride at Mount Faber Loop', 'Ride to the top and enjoy city skyline views.', 'Mount Faber Park', 'HarbourFront MRT', 1.2750, 103.8197, 100, '2025-12-27 08:30:00', '2025-12-27 11:30:00', 'The Trishaw Ride at Mount Faber Loop provides seniors with breathtaking views of Singapore’s skyline from one of the city’s highest points. The ride navigates gently through Mount Faber Park, with volunteers ensuring safety and comfort along well-maintained paths. Participants enjoy panoramic vistas of the harbor, Sentosa, and the southern islands, making it an ideal opportunity for photography, relaxation, and mindfulness. Along the route, volunteers share interesting facts about the park’s history, local flora, and urban planning significance, enriching the ride with educational insights. The loop’s gradual inclines allow seniors to experience a sense of adventure without physical strain, while strategically placed resting points offer opportunities to pause and absorb the scenery. The ride emphasizes wellness, gentle physical activity, and social engagement, as participants interact with volunteers and fellow riders. The combination of natural beauty, iconic city views, and volunteer guidance creates a memorable experience that nurtures both body and mind, offering seniors a unique perspective of Singapore’s landscape from a safe and comfortable trishaw ride.'),
('Trishaw Ride at Jurong Central Park', 'A joyful community event for all to enjoy.', 'Jurong Central Park', 'Boon Lay MRT', 1.3333, 103.7425, 100, '2025-12-28 09:00:00', '2025-12-28 12:00:00', 'The Trishaw Ride at Jurong Central Park offers seniors a joyful and community-focused outdoor experience. The ride takes participants along scenic trails surrounded by green spaces, playgrounds, and communal areas where locals often gather for leisure activities. Volunteers guide seniors along safe and accessible paths, highlighting the park’s features, such as ponds, open lawns, and heritage landmarks. Participants can enjoy the fresh air, observe local wildlife, and interact with volunteers who share insights about the park’s history, conservation efforts, and community initiatives. The gentle pace ensures that seniors can relax, take photographs, and appreciate the environment without physical strain. The ride promotes social engagement, wellness, and light physical activity, offering opportunities for conversation and connection among participants. Pauses at scenic spots or benches allow seniors to enjoy refreshments and share experiences with volunteers and fellow riders. This trishaw ride provides a wholesome outing that combines recreation, nature, and social interaction, leaving seniors refreshed, engaged, and connected to the community and the natural surroundings.'),
('Trishaw Ride at Esplanade Waterfront', 'Ride by the bay and enjoy Marina skyline sights.', 'Esplanade Waterfront', 'Esplanade MRT', 1.2896, 103.8551, 100, '2025-12-29 17:00:00', '2025-12-29 19:00:00', 'The Trishaw Ride at Esplanade Waterfront provides seniors with a scenic journey along Singapore’s iconic Marina Bay waterfront. The route follows wide, accessible paths with views of the city skyline, the bay, and nearby cultural landmarks such as the Esplanade Theatres and Merlion Park. Volunteers guide seniors along a gentle pace, ensuring safety and comfort while providing commentary about the waterfront’s history, development, and architectural highlights. Riders can enjoy the sparkling waters, open promenades, and occasional performances or activities along the bay, creating a dynamic yet relaxing atmosphere. Strategic stopping points allow seniors to take photographs, engage in light conversation, or simply pause and enjoy the surroundings. The ride emphasizes wellness, social interaction, and appreciation of Singapore’s urban landscape, offering a combination of leisure, culture, and gentle exercise. Volunteers also highlight points of ecological and historical interest, enhancing participants’ understanding of the area. This experience provides a memorable and engaging outing, fostering relaxation, enjoyment, and a sense of connection to both nature and the vibrant urban environment.'),
('Trishaw Ride at Civic District Night Lights', 'Evening trishaw route showcasing festive light-ups.', 'Civic District', 'City Hall MRT', 1.2904, 103.8515, 100, '2025-12-30 19:30:00', '2025-12-30 21:30:00', 'The Trishaw Ride at Civic District Night Lights offers seniors a unique evening experience exploring Singapore’s historical and cultural heart. The ride follows illuminated streets, passing iconic landmarks such as the National Gallery, Victoria Theatre, and the Padang, showcasing beautiful light displays and architectural highlights. Volunteers guide seniors along safe, well-lit paths, ensuring comfort and security throughout the journey. Riders can enjoy the ambience of the city at night, with sparkling lights reflecting on buildings and waterfronts, creating a magical and memorable atmosphere. Along the route, volunteers provide interesting commentary on the history of the Civic District, its significance in Singapore’s development, and the stories behind the heritage buildings. The gentle pace allows seniors to relax, take photographs, and engage in conversation, enhancing both social interaction and mental stimulation. This evening trishaw ride combines sightseeing, leisure, and light physical activity, providing a safe and enriching experience. Participants leave with a sense of connection to the city, appreciation for its history and architecture, and a delightful memory of exploring Singapore under a canopy of night lights.');

CREATE TABLE attendance (
    id INT IDENTITY(1,1) PRIMARY KEY,
    userId INT NOT NULL FOREIGN KEY REFERENCES users(userId),
    eventId INT NOT NULL FOREIGN KEY REFERENCES events(eventId),
    check_in_time DATETIME2 NULL,
    check_in_lat FLOAT NULL,
    check_in_lon FLOAT NULL,
    check_out_time DATETIME2 NULL,
    check_out_lat FLOAT NULL,
    check_out_lon FLOAT NULL,
    status NVARCHAR(50) NULL -- On Time/Late
);

CREATE TABLE bookedevents (
    bookingId INT IDENTITY(1,1) PRIMARY KEY,
    userId INT NOT NULL,
    eventId INT NOT NULL,
    bookingDate DATETIME DEFAULT GETDATE(),

    FOREIGN KEY (userId) REFERENCES users(userId),
    FOREIGN KEY (eventId) REFERENCES events(eventId)
);
CREATE TABLE eventSignups (
    id INT IDENTITY(1,1) PRIMARY KEY,
    eventId INT NOT NULL,
    userId INT NOT NULL,
    signupDate DATETIME DEFAULT GETDATE(),
    FOREIGN KEY (eventId) REFERENCES events(eventId),
    FOREIGN KEY (userId) REFERENCES users(userId)
);
